				array.add(new MyItem(beacon.getJSONObject(i).getString("key"), beacon.getJSONObject(i).getString("image"), beacon.getJSONObject(i).getString("map"), beacon.getJSONObject(i).getString("name"), beacon.getJSONObject(i).getString("explain"), 100));
